package DayOne;

public class BitwiseOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Bitwise operators perform bit by bit// encryption and data compression
		int e=8;
		int f=2;
		System.out.println(e&f);
		System.out.println(e>>f);
		System.out.println(e>>>f);

	}

}
